import { AllPosts } from '../services/PostService';

export const getAllPosts = () => (dispatch) => {
		AllPosts().then(res => {
			console.log(res);

			if(res.posts == "success"){

				dispatch({type: 'SIGNUP_SUCCESS'});
			} else {
				dispatch({type:'SIGNUP_ERROR', res});
			}
		}

		).catch(error => {
			console.log(error)
			dispatch({type:'CODE_ERROR', error})
		});

}
