import React, { Component } from 'react';

import { connect } from 'react-redux'
import { TextField, TextareaAutosize } from '@material-ui/core';
import Button from '@material-ui/core/Button';

import { addPost } from '../../store/actions/PostAction';
import MyUploader from '../../components/MyUploader'

class create extends Component {
	constructor(props) {
        super(props);
        this.state = {
            title: '',
            text: '',
            media: []
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    
    // resetForm = (e) => {
    //     this.setState({
    //         title: '',
    //         text : '',
    //         media: []
    //     })
    // }

	handleChange = (e) => {
        const files = e.target.files || e.dataTransfer.files;
		this.setState({
			[e.target.name] :e.target.value ,
		});
    	this.createImage(files[0]);
    }

    createImage(file) {
      let reader = new FileReader();
      reader.onload = (e) => {
        this.setState({
          media: e.target.result
        })
      };
      reader.readAsDataURL(file);
    }


    handleSubmit = (e) =>{
        e.preventDefault();
        const formdata = new FormData()
        formdata.append('media', e.target.files[0])
        console.log(formdata); 

        this.props.addPost(formdata);
        // debugger;

        // this.props.history.push('/');
    }

    render() {
        const { media } = this.props
	    return (
            <>
            <form noValidate autoComplete="off">
                <TextField 
                    id="email" name="title" type="text" 
                    label="Title"
                    value={this.state.title} 
                    fullWidth 
                    onChange={this.handleChange}/>
                <TextareaAutosize 
                    aria-label="maximum width" 
                    name="text" 
                    label="Text"
                    style={{width: '100%', marginTop:'10px'}}
                    rowsMin="10"
                    value={this.state.text}
                    onChange={this.handleChange}
                    placeholder="Text here" />

                <input type="file" multiple name="files" onChange={this.handleChange}/>
                <Button  
                    style={{marginTop: '10px'}} variant="contained" 
                    color="primary" onClick={this.handleSubmit}>
                    Add post
                </Button>
            </form>
            </>
        );
	}
}

const mapStatesToProps = (state) => {
	return {
        media:state.post.media
	};
}

const mapDispatchToProps = (dispatch) => {
	return {
		addPost:(postObject) => dispatch(addPost(postObject))
	}
}

export default connect(mapStatesToProps, mapDispatchToProps)(create)
