import React, { Component } from 'react';
import { Post } from '../components/Post';
import { getAllPosts } from '../store/actions/PostAction';
// redux
import { connect } from 'react-redux'

class home extends Component {
	constructor(props) {
	    super(props);
	}

	componentDidMount = async() => {
		await this.props.getAllPosts();
		console.log(this.props);
	}

    render() {
    	const { loadPosts } = this.props;

		const posts = [
		  {id: 1, title: 'Hello World', content: 'Welcome to learning React!'},
		  {id: 2, title: 'Installation', content: 'You can install React from npm.'}
		];
	    return (

		    <div> 
		    	{loadPosts && loadPosts.hasOwnProperty('allPosts')? loadPosts.allPosts.map(row => (
		    		<p> {row.text} </p> 
		    		)) : null }

		        <Post loadPosts={loadPosts}/>
		    </div>
	    );
	}

}


const mapDispatchToProps = (dispatch) => {
	return {
		getAllPosts:() => dispatch(getAllPosts())
	}
}

const mapStatesToProps = (state) => {
	return { 
		    loadPosts:state.post.loadPosts
		};
}

export default connect(mapStatesToProps, mapDispatchToProps)(home)
