import React, { Component } from 'react';

import { connect } from 'react-redux'
import { TextField, TextareaAutosize } from '@material-ui/core';
import Button from '@material-ui/core/Button';

import 'react-dropzone-uploader/dist/styles.css'
import Dropzone from 'react-dropzone-uploader'

import { addPost } from '../../store/actions/PostAction';
import MyUploader from '../../components/MyUploader'

class create extends Component {
	constructor(props) {
        super(props);
        this.state = {
            title: '',
            text: '',
            media: []
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    // specify upload params and url for your files
    getUploadParams = ({ meta }) => {
        console.log(meta)
        // this.setState({
        //     media : [...this.state.media, meta]
        // });
        return { url: 'https://httpbin.org/post' } 
    }
    
    // called every time a file's `status` changes
    handleChangeStatus = ({ meta, file }, status) => {
       if (status == 'done') {
         let media = this.state.media;
         media.push(file);
         this.setState({
            media
         })
         console.log('new state media');
         console.log(media); 

       } 
       console.log(file);
       console.log(status) 
    }  

	handleChange = (e) => {
		this.setState({
			[e.target.name] :e.target.value,
		});
    }

    handleSubmit = (e) =>{
        e.preventDefault();
        const formData = new FormData()

        Object.keys(this.state.media).forEach((file) => {
            formData.append('media', file);
        });


        this.props.addPost(formData);

        // this.props.history.push('/');
    }

    render() {
        const { media } = this.props
	    return (
            <>
            <form noValidate autoComplete="off" encType="multipart/form-data">
                <TextField 
                    id="email" name="title" type="text" 
                    label="Title"
                    value={this.state.title} 
                    fullWidth 
                    onChange={this.handleChange}/>
                <TextareaAutosize 
                    aria-label="maximum width" 
                    name="text" 
                    label="Text"
                    style={{width: '100%', marginTop:'10px'}}
                    rowsMin="10"
                    value={this.state.text}
                    onChange={this.handleChange}
                    placeholder="Text here" />

                <input type="file" multiple name="files" onChange={this.handleChange}/>

                <Dropzone
                    getUploadParams={this.getUploadParams}
                    onChangeStatus={this.handleChangeStatus}
                    accept="image/*,audio/*,video/*"
                />

                <Button  
                    style={{marginTop: '10px'}} variant="contained" 
                    color="primary" onClick={this.handleSubmit}>
                    Add post
                </Button>
            </form>
            </>
        );
	}
}

const mapStatesToProps = (state) => {
	return {
        media:state.post.media
	};
}

const mapDispatchToProps = (dispatch) => {
	return {
		addPost:(postObject) => dispatch(addPost(postObject))
	}
}

export default connect(mapStatesToProps, mapDispatchToProps)(create)
